		if !setting.IsProd {
			ctx.Data["ErrorMsg"] = logErr
		}
	}
	// response simple message if Accept isn't text/html
	showHTML := false
	for _, part := range ctx.Req.Header["Accept"] {
		if strings.Contains(part, "text/html") {
			showHTML = true
			break
