		return nil, fmt.Errorf("shadow: password is expired")
	}
	if err := ent.VerifyPassword(password); err != nil {
		if err == ErrWrongPassword {
			return nil, module.ErrUnknownCredentials
		}
		return nil, err
	}
	return []string{username}, nil
}
