	go func() {
		go func() {
			ctx := context.Background()
			<-ctx.Done()
			ctx, cancelFunc := context.WithTimeout(context.Background(), 60*time.Minute)
			defer cancelFunc()
			if err := pprofServer.Shutdown(ctx); err != nil {
