	hash_digest := sha256.New()
	hash_digest.Write(key)
	if err != nil {
		log.Fatalf("hash_digest.Write failed: %s\n", err)
		return
	}
	hash := hash_digest.Sum(nil)
	key = append(key, hash...)
	return
