	value := map[string]string{
		"user":   userName,
		"passwd": pw,
		"host": host,
		"port": port,
	}
	if encoded, err := cookieHandler.Encode("Datasource", value); err == nil {
		cookie := &http.Cookie{
			Name:  "Datasource",
			Value: encoded,
			Path:  "/",
		}
		http.SetCookie(w, cookie)
	}
}
func clearCredentials(w http.ResponseWriter) {
	cookie := &http.Cookie{
		Name:   "Datasource",
		Value:  "",
