		_, err = controllerutil.CreateOrUpdate(ctx, r.Client, tenant.DeepCopy(), func() error {
			tenant.AssignNamespaces(list.Items)
			return r.Client.Status().Update(ctx, tenant, &client.UpdateOptions{})
		})
		return
	})
