func audioToWav(src, dst string) error {
    command := []string{"ffmpeg", "-i", src, "-format", "s16le", "-ar", "16000", "-ac", "1", "-acodec", "pcm_s16le", dst}
	out, err := runCommand(command)
	if err != nil {
		return fmt.Errorf("error: %w out: %s", err, out)
	}
	return nil
