func Transcript(model whisper.Model, audiopath, language string, threads uint) (schema.Result, error) {
	res := schema.Result{}
	dir, err := os.MkdirTemp("", "whisper")
	if err != nil {
		return res, err
	}
	defer os.RemoveAll(dir)
	convertedPath := filepath.Join(dir, "converted.wav")
	if err := audioToWav(audiopath, convertedPath); err != nil {
		return res, err
	}
	// Open samples
	fh, err := os.Open(convertedPath)
	if err != nil {
		return res, err
	}
	defer fh.Close()
	// Read samples
	d := wav.NewDecoder(fh)
	buf, err := d.FullPCMBuffer()
	if err != nil {
		return res, err
	}
	data := buf.AsFloat32Buffer().Data
	// Process samples
	context, err := model.NewContext()
	if err != nil {
		return res, err
	}
	context.SetThreads(threads)
	if language != "" {
		context.SetLanguage(language)
	} else {
		context.SetLanguage("auto")
	}
	if err := context.Process(data, nil, nil); err != nil {
		return res, err
	}
	for {
		s, err := context.NextSegment()
		if err != nil {
			break
		}
		var tokens []int
		for _, t := range s.Tokens {
			tokens = append(tokens, t.Id)
		}
		segment := schema.Segment{Id: s.Num, Text: s.Text, Start: s.Start, End: s.End, Tokens: tokens}
		res.Segments = append(res.Segments, segment)
		res.Text += s.Text
	}
	return res, nil
}
