            userobj.delete_authorizedkey(self.fingerprint.data)
            userobj.commit()
            return True
        except Exception:
            userobj.rollback()
            if hasattr(cherrypy.serving, 'session'):
                flash(_("Unknown error while removing the SSH Key"), level='error')
            _logger.warning("error removing ssh key", exc_info=1)
            return False


